workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class AmnestySMCListController extends ListController
{
    @Service('LedgerAmnestyCaptureSMCService')
    def svc;

    String serviceName = 'LedgerAmnestyCaptureSMCService';
    String entityName = 'ledgeramnesty:capture:smc';

    Map createPermission = [domain: 'AUDIT', role: 'AUDIT_AMNESTY'];


    void beforeGetColumns(Map params) {
        params.state = selectedOption?.state;
    }

    def selectedOption;
    def optionsModel = [
        fetchList: { 
            return svc.getStates();; 
        },
        onselect: {o-> 
            query.state = selectedOption?.state; 
            reloadAll(); 
        }
    ] as ListPaneModel; 
}
